DELIMITER |

CREATE PROCEDURE CallMap

(IN ID INT(2), OUT content varchar(250000) )

BEGIN
	SELECT content_map INTO content
	FROM maps 
	WHERE id_map = ID;
END |
DELIMITER ;

CREATE TABLE IF NOT EXISTS maps (id_map INTEGER NOT NULL PRIMARY KEY, content_map VARCHAR(3200) NOT NULL);
